<?phpsession_start(); include("../php/connect.php");  $query_str = $_POST['query']	;					$str1 = "select * from product ".$query_str." ORDER BY price ASC";	//echo $str1;	$sql = mysql_query($str1);						$responses = array();	if(mysql_num_rows($sql) > 0)	{		while($row=mysql_fetch_array($sql)) 		{			$response = array('product_id' => $row['product_id'],'product_code' => $row['product_code'],'title' => $row['title'],'category' => $row['category'],'subcategory' => $row['subcategory'],'color' => $row['color'],'price' => $row['price']						,'discount_percent' => $row['discount_percent'],'hit_count' => $row['hit_count'],'gender' => $row['gender']);								$idProduct = $row['product_id'];			$sqlImg = mysql_query("select * from product_image WHERE product_id='".$idProduct."'");						$countImg=mysql_num_rows($sqlImg);			$response['img_count'] = $countImg;			$count = 0;			if(mysql_num_rows($sqlImg) > 0)			{				while($row=mysql_fetch_array($sqlImg)) 				{										$response['image_path'.$count] = $row['image_path'];					$count++;				}			}						$sqlQty = mysql_query("select * from product_size_quantity WHERE product_id='".$idProduct."'");			$countQty=mysql_num_rows($sqlQty);						$response['qty_count'] = $countQty;			$count = 0;			if(mysql_num_rows($sqlQty) > 0)			{				while($row=mysql_fetch_array($sqlQty)) 				{										$response['id'.$count] = $row['id'];					$response['size_type'.$count] = $row['size_type'];					$response['dimension'.$count] = $row['dimension'];					$response['quantity'.$count] = $row['quantity'];					$response['availability'.$count] = $row['availability'];					$count++;				}			}		$responses[] = $response;		}		echo json_encode($responses);		//return json_encode($responses);					}	else	{		$response = array('product_id' => '-1','product_code' => '-1','title' => 'empty','category'=>'empty');		$responses[] = $response;		echo json_encode($responses);	}				 			?>