<?phpsession_start(); include("connect.php");  $query_str = $_POST['query']	;					$str1 = "select * from product "+$query_str;	$sql = mysql_query($str1);						$responses = array();	if(mysql_num_rows($sql) > 0)	{		while($row=mysql_fetch_array($sql)) 		{			$response = array('product_id' => $row['product_id'],'product_code' => $row['product_code'],'title' => $row['title'],'category' => $row['category'],'subcategory' => $row['subcategory'],'color' => $row['color'],'price' => $row['price']						,'discount_percent' => $row['discount_percent'],'hit_count' => $row['hit_count'],'gender' => $row['gender']);			$responses[] = $response;							$sqlImg = mysql_query("select * from product_image WHERE product_id='"+$row['product_id']+"'");						$countImg=mysql_num_rows($sqlImg);			$responseImg = array('img_count' => $countImg);			$responses[] = $responseImg;			if(mysql_num_rows($sqlImg) > 0)			{				while($row=mysql_fetch_array($sqlImg)) 				{					$response = array('image_path' => $row['image_path']);					$responses[] = $response;				}			}						$sqlQty = mysql_query("select * from product_size_quantity WHERE product_id='"+$row['product_id']+"'");			$countQty=mysql_num_rows($sqlQty);			$responseQty = array('qty_count' => $countQty);			$responses[] = $responseQty;			if(mysql_num_rows($sqlQty) > 0)			{				while($row=mysql_fetch_array($sqlQty)) 				{					$response = array('id'=>$row['id'],'size_type' => $row['size_type'],'dimension' => $row['dimension'],'quantity' => $row['quantity'],'availability' => $row['availability']);					$responses[] = $response;				}			}				}		echo json_encode($responses);		//return json_encode($responses);					}	else	{		$response = array('product_id' => '-1','product_code' => '-1','title' => 'empty','category'=>'empty');		$responses[] = $response;		echo json_encode($responses);	}				 			?>